import React, { useState } from 'react';
import { hot } from 'react-hot-loader/root';
import styled from 'styled-components';
import { Link, useCurrentRoute } from 'react-navi';

import MarketingLayout from 'layouts/MarketingLayout';
import { FadeIn, FilledButton, PageHead } from 'components/Marketing';
import mixpanel from 'mixpanel-browser';
import { Routes } from 'utils/constants';
import useLanguage from 'hooks/useLanguage';
import { getColor, marketingTheme } from 'styles/theme';
import AnimationLottie from './AnimateConvas';

import { ReactComponent as BorrowIcon } from 'images/landing/borrow_block.svg';
import { ReactComponent as SaveIcon } from 'images/landing/save_block.svg';
import { ReactComponent as UsdlIcon } from 'images/landing/land_usdl.svg';
import { ReactComponent as LpgIcon } from 'images/landing/land_fl.svg';
import { ReactComponent as CommunityIcon } from 'images/landing/land_com.svg';
import { ReactComponent as ImgPoolsUsdl } from 'images/landing/pools_to_usdl_full.svg';
import { ReactComponent as Play } from 'images/landing/pl_vdo.svg';
import { ReactComponent as RdPoint } from 'images/landing/rd_point.svg';
import Line from 'images/landing/line.png';
import BannerFl from 'images/landing/banner_fl.svg';
import BannerBinance from 'images/landing/banner_binance.svg';

import { Box, Flex, Text, Grid } from '@makerdao/ui-components-core';
import Modal from 'react-modal';
import ReactPlayer from 'react-player';

const Content = ({ children }) => (
  <Box p={{ s: `0 ${marketingTheme.mobilePaddingX}`, l: '0 32px' }}>
    <Box maxWidth="1140px" mx="auto">
      {children}
    </Box>
  </Box>
);

const ImgBox = styled.div`
  text-align: center;
  @media only screen and (min-device-width: 769px) and (max-device-width: 1024px) {
    height: 160px;
  }
  @media (max-width: 768px) {
    height: 180px;
  }
`;
const SpaceBox = styled.div`
  width: 100%;
  height: 50px;
`;

const Cards = (() => {
  const CardsContainer = styled(Box)`
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    margin-right: auto;
    margin-left: auto;
    padding-bottom: 48px;

    :after {
      content: ' ';
      display: block;
      position: absolute;
      z-index: -1;
      bottom: 0;
      width: 93%;
      left: 3.5%;
      height: 91%;
    }

    @media (max-width: 768px) {
      :after {
        content: none;
      }
      padding-bottom: 0px;
    }
  `;

  const Card = styled.div`
    overflow: hidden;
    width: 48%;
    position: relative;
    flex-shrink: 1;
    text-align: left;
    padding: 60px 100px 60px 55px;
    @media only screen and (min-device-width: 768px) and (max-device-width: 1024px) {
      padding: 40px 30px 40px 30px;
    }
    @media (max-width: 767px) {
      margin-bottom: 24px;
      width: 100%;
      padding: 40px 30px 40px 30px;
    }
    .buttonContainer {
      border-radius: 50px;
      margin-top: 40px;
      display: inline-block;
      transition: all 0.15s ease;
      padding-bottom: 0;
      cursor: pointer;
      :hover {
        ${FilledButton} {
          background-color: ${getColor('cardBg')};
          color: ${getColor('cayn')};
          bprder-color: ${getColor('cayn')};
        }
      }
    }
    .buttonContainer_v {
      background-color: ${getColor('cardBg')};
      border-radius: 50px;
      margin-top: 40px;
      display: inline-block;
      transition: all 0.15s ease;
      padding-bottom: 0;
      cursor: pointer;
      :hover {
        ${FilledButton} {
          background-color: ${getColor('cardBg')};
          color: ${getColor('cayn')};
          bprder-color: ${getColor('cayn')};
        }
      }
    }

    ${FilledButton} {
      display: inline-flex;
      padding: 13px 35px 13px;
      height: unset;
      line-height: 19px;
      text-decoration: none;
    }
  `;

  return props => {
    const { url } = useCurrentRoute();
    const { lang } = useLanguage();

    return (
      <CardsContainer {...props}>
        <Card
          style={{
            background: getColor('cardBg'),
            borderRadius: '20px'
          }}
        >
          <BorrowIcon />
          <Text.h3>{lang.landing_page.borrow_card.title}</Text.h3>
          <Text.h5>{lang.landing_page.borrow_card.description}</Text.h5>
          <div className="buttonContainer">
            <Link
              href={`/${Routes.BORROW}${url.search}`}
              prefetch={true}
              onClick={() => {
                mixpanel.track('btn-click', {
                  id: 'BorrowUSDFL',
                  product: 'freeliquid-landing'
                });
              }}
            >
              <FilledButton>
                {lang.landing_page.borrow_card.button}
              </FilledButton>
            </Link>
          </div>
        </Card>
        <Card
          style={{
            background: getColor('cardBg'),
            borderRadius: '20px'
          }}
        >
          <SaveIcon />
          <Text.h3>{lang.landing_page.save_card.title}</Text.h3>
          <Text.h5>{lang.landing_page.save_card.description}</Text.h5>
          <div className="buttonContainer">
            <Link
              href={`/${Routes.SAVE}${url.search}`}
              prefetch={true}
              onClick={() => {
                mixpanel.track('btn-click', {
                  id: 'SaveUSDFL',
                  product: 'freeliquid-landing'
                });
              }}
              className="button-link"
            >
              <FilledButton>{lang.landing_page.save_card.button}</FilledButton>
            </Link>
          </div>
        </Card>
      </CardsContainer>
    );
  };
})();

const TextDiv = styled.div`
  padding: 0px 0px 30px;
  @media (max-width: 768px) {
    width: 100%;
    padding: 30px 15px;
  }

  .text-desc {
    font-size: 20px;
    color: ${getColor('greyText')};
    line-height: 34px;
  }
`;
const Blocks = styled(Flex)`
  margin: 0px auto 69px;
  flex-wrap: wrap;
  text-align: left;
  align-items: center;
  justify-content: revert;
  @media (min-width: ${props => props.theme.breakpoints.m}) {
  }
  @media (max-width: 768px) {
    width: 100%;
    text-align: center;
    flex-direction: column-reverse;
  }
  @media only screen and (min-device-width: 769px) and (max-device-width: 1024px) {
    padding: 0px 25px;
  }
`;
const Blocks2 = styled(Flex)`
  margin: 0px auto 69px;
  flex-wrap: wrap;
  text-align: left;
  align-items: center;
  justify-content: revert;
  padding: 0px 120px;
  @media (min-width: ${props => props.theme.breakpoints.m}) {
  }
  @media only screen and (min-device-width: 769px) and (max-device-width: 1024px) {
    padding: 20px 25px;
  }
  @media (max-width: 768px) {
    width: 100%;
    text-align: center;
    flex-direction: column-reverse;
    padding: 0px 10px;
  }
  @media (max-width: 768px) {
    width: 100%;
    text-align: center;
    flex-direction: column-reverse;
    padding: 0px 10px;
    a.linkContract,
    a.linkContract2 {
      font-size: 13px !important;
    }
  }
  @media (max-width: 321px) {
    a.linkContract,
    a.linkContract2 {
      font-size: 11px !important;
    }
  }
  a.linkContract {
    color: rgb(122 193 255);
    font-size: 16px;
    text-transform: lowercase;
  }
  a.linkContract:hover {
    color: #a3b2cf;
  }
  a.linkContract2 {
    color: #00dcdc;
    font-size: 16px;
    text-transform: lowercase;
  }
  a.linkContract2:hover {
    color: #a3b2cf;
  }
`;
const BlockBorder = styled.div`
  width: 1px;
  height: 260px;
  border-left: 1px solid ${getColor('border')};
  @media only screen and (min-device-width: 769px) and (max-device-width: 1024px) {
    display: block;
  }
  @media (max-width: 768px) {
    display: none;
  }
`;
const BlocksDiv = styled.div`
  width: 49%;
  padding: 40px 110px 40px 0px;
  justify-content: center;
  align-items: revert;
  .btn_vdo svg {
    cursor: pointer;
    transition: all 0.15s ease;
  }
  .btn_vdo svg:hover {
    fill: #00dcdc;
    transform: scale(1.2);
  }
  @media only screen and (min-device-width: 769px) and (max-device-width: 1024px) {
    padding: 20px 0px 30px 0px;
  }
  @media (max-width: 768px) {
    width: 100%;
    padding: 30px 15px;

    @media (min-width: 736px) {
      width: 100%;
      padding: 30px 15px;
    }
  }
`;
const BlocksDiv2 = styled.div`
  width: 49%;
  @media only screen and (min-device-width: 769px) and (max-device-width: 1024px) {
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    .video_bg {
      width: 300px;
      height: 250px;
    }
    .all_img {
      width: 360px;
      height: 270px;
      margin-top: -55px;
    }
    .usdl_svg {
      height: 160px;
    }
    .lpg_svg {
      height: 160px;
    }
    .comm_svg {
      height: 160px;
    }
  }
  @media (max-width: 768px) {
    width: 100%;
    min-height: 320px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    .video_bg {
      width: 300px;
      height: 250px;
    }
    .all_img {
      height: 240px;
      margin-top: -60px;
    }
    .usdl_svg {
      height: 180px;
    }
    .lpg_svg {
      height: 180px;
    }
    .comm_svg {
      height: 180px;
    }
  }
`;

const BlockPoolsUsdl = styled.div`
  display: flex;
  background: ${getColor('cardBg')};
  border-radius: 40px;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  margin-bottom: 80px;
  .block_img {
    width: 47%;
  }
  .block_text {
    width: 49%;
    padding: 0px 100px 0px 100px;
    text-align: left;
    font-size: 18px;
    line-height: 32px;
    color: ${getColor('greyText')};
  }
  @media only screen and (min-device-width: 768px) and (max-device-width: 1024px) {
    flex-direction: row;
    padding: 20px 0px 30px 0px;
    .block_img svg {
      width: 100%;
    }
    .block_img {
      width: 49%;
    }
    .block_text {
      width: 49%;
      padding: 0px 30px 0px 30px;
    }
  }
  @media (max-width: 767px) {
    width: 100%;
    .block_img svg {
      width: 100%;
      margin-top: -50px;
    }
    .block_img {
      width: 100%;
    }
    .block_text {
      width: 100%;
      padding: 15px 40px 55px;
      text-align: center;
    }
    flex-direction: column;

    @media (min-width: 736px) {
      width: 100%;

      .block_img {
        width: 100%;
      }
      .block_text {
        width: 100%;
        padding: 15px 40px 55px;
        text-align: center;
      }
    }
  }
`;

const ButtonFlex = styled.div`
  font-size: 18px;
  display: grid;
  grid-template-columns: 1fr 1fr;
  justify-content: start;
  align-items: center;
  grid-column-gap: 20px;
  @media (max-width: 768px) {
    justify-content: space-between;
    grid-template-columns: 1fr;
    grid-row-gap: 20px;
  }
  @media (min-width: 40em) {
    justify-content: center;
    grid-template-columns: 1fr 1fr;
  }
  @media only screen and (orientation: landscape) and (max-device-width: 768px) {
    grid-template-columns: 1fr;
    grid-row-gap: 20px;
  }
`;
const TitleCard = styled.div`
  font-size: 40px;
  font-weight: bold;
  color: ${getColor('whiteText')};
`;
const RoadMap = styled.div`
  background: ${getColor('cardBg')};
  border-radius: 20px;
  padding: 35px 45px;
  .rd_img {
    padding: 65px 0px 0px;
    margin-bottom: -18px;
  }
  .div_wrap {
    display: flex;
  }
  .rd_block {
    width: 50%;
    text-align: left;
    padding-left: 120px;
  }
  .rd_block2 {
    width: 50%;
    text-align: left;
    padding-left: 45px;
  }
  .rd_block3 {
    display: grid;
    grid-template-columns: 1fr 8fr;
  }
  .rd_point {
    margin-top: 8px;
  }
  .rd_point2 {
    width: 26px;
    height: 26px;
  }
  .rd_block4 {
    margin-top: 85px;
    padding-right: 100px;
  }
  .rd_block4 p {
    font-size: 16px;
  }
  @media (max-width: 768px) {
    padding: 35px 25px;
    .div_wrap {
      flex-direction: column;
    }
    .rd_point2 {
      display: none;
    }
    .rd_img {
      display: none;
    }
    .rd_block {
      width: 100%;
      text-align: left;
      padding-left: 0px;
    }
    .rd_block2 {
      width: 100%;
      text-align: left;
      padding-left: 0px;
    }
    .rd_block4 {
      padding-right: 0px;
    }
  }
`;
const Banner = styled.div`
  .wrap_banner_fl {
    background-image: url(${BannerFl});
    background-color: #3a1fb9;
    padding: 30px 230px 30px 35px;
    background-size: 62%;
    background-repeat: no-repeat;
    display: grid;
    grid-template-columns: 1fr;
    -webkit-align-items: left;
    -webkit-box-align: left;
    -ms-flex-align: left;
    align-items: left;
    background-position: 330px -135px;
    border-radius: 20px;
    text-align: left;
  }
  .wrap_banner_binance {
    background-image: url(${BannerBinance});
    background-color: #343840;
    padding: 30px 170px 30px 35px;
    background-size: 75%;
