import React from 'react';
import { Flex, Grid, Text } from '@makerdao/ui-components-core';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import FadeIn from './FadeIn';
import { getColor } from 'styles/theme';

const StyledGrid = styled(Grid)`
  text-align: left;
  margin-left: auto;
  margin-right: auto;

  ${Flex} {
    flex-direction: column;
  }

  ${Text.h4} {
    margin: 25px 0 15px;
    letter-spacing: 0.3px;
  }

  ${Text} {
    display: block;
    letter-spacing: unset;
    line-height: 31px;
    font-size: 17px;
    color: ${getColor('greyText')}
  }
`;

const Features = ({ features, ...props }) => {
  return (
    <StyledGrid
      gridTemplateColumns={{ s: '1fr', l: '1fr 1fr' }}
      maxWidth={{ s: '330px', l: '1120px' }}
      gridRowGap="118px"
      gridColumnGap="120px"
      pl={{ s: '23px', m: 0 }}
      {...props}
    >
      {features.map(point => (
        <FadeIn key={point.title} triggerOffset={160} moveDistance="25%">
          <Flex>
            {point.img}
            <Text.h4>{point.title}</Text.h4>
            <Text>{point.content}</Text>
          </Flex>
        </FadeIn>
      ))}
    </StyledGrid>
  );
};

Features.propTypes = {
  features: PropTypes.arrayOf(
    PropTypes.shape({
      img: PropTypes.any,
      title: PropTypes.any,
      content: PropTypes.any
    })
  )
};

export default Features;
