import React from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import MakerLogo from "./assets/makerLogo.svg";

const LogoWrapper = styled.div`
  & .left-arch {
    stroke: ${props => props.theme.logo.color};
  }

  & .left-arch-ends {
    fill: ${props => props.theme.logo.color};
  }

  & .right-arch {
    stroke: ${props => props.theme.logo.color};
  }

  & .right-arch-ends {
    fill: ${props => props.theme.logo.color};
  }

  & .text {
    stroke: ${props => props.theme.logo.color};
    fill: ${props => props.theme.logo.color};
  }
`;

LogoWrapper.defaultProps = {
  theme: {
    logo: {
      color: "#30BD9F",
    },
  },
};
/**
 * MakerDAO Logo
 */
const Logo = () => (
  <LogoWrapper>
    <MakerLogo />
  </LogoWrapper>
);

Logo.propTypes = {
  theme: PropTypes.shape({
    logo: PropTypes.shape({
      color: PropTypes.string,
    }),
  }),
};

export default Logo;
